INSERT INTO authors (name, comment, small_photo_filename, photo_filename) VALUES ('Глебов Макс', 'Макс Глебов – творческий псевдоним. Я родился на юге России, но уже более тридцати лет живу в Санкт-Петербурге. Желание создавать фантастические произведения впервые появилось у меня еще в детстве, после прочтения бессмертных книг Герберта Уэллса, братьев Стругацких и Станислава Лема, но тогда, перечитывая написанное и невольно сравнивая свои тексты с произведениями классиков жанра, я четко осознавал – не тяну. Любовь к фантастике я пронес через всю сознательную жизнь, но уже во вполне зрелом возрасте вдруг стал понимать, что почти все, что я читаю – не совсем то, что мне бы хотелось видеть. Пришлось вновь попробовать самому. Надеюсь, получилось что-то внятное.','/src/img/author_small_002.jpg','/src/img/author_full_002.jpg'); 
INSERT INTO books (name, comment, publishing_house_id ,year, ISBN, characteristics) VALUES ('Луна цвета стали', 'Связь с Лунной базой восстановлена, что открывает для лейтенанта Ирса колоссальные возможности и доступ к недоступным ранее технологиям. Теперь он в режиме реального времени может отслеживать расстановку сил врага по всему земному шару. Казалось бы, вот он, залог победы в войне. Но все оказывается куда сложнее…
