SELECT @P_id_ph := id FROM publishing_houses WHERE name = 'Издательство «Эксмо»';INSERT INTO books (name, comment, publishing_house_id ,year, ISBN, characteristics) VALUES ('Истории Асгарда. Трилогия', 'Тор, Сиф и Троица Воинов – лучшие защитники Асгарда. Героям предстоит проявить всю свою храбрость, хитрость и силу воли, чтобы спасти родную землю от опасных врагов.
