SELECT @P_id_ph := id FROM publishing_houses WHERE name = 'Издательство «АСТ»';INSERT INTO books (name, comment, publishing_house_id ,year, ISBN, characteristics) VALUES ('Тройная мудрость', 'Тысячи теплых отзывов от людей абсолютно разных социальных статусов и профессий: влиятельных политиков, бизнесменов, известных психологов, педагогов, домохозяек и студентов говорят, что их жизнь кардинально изменилась в лучшую сторону после прочтения этих произведений. Мощнейшие книги! Каждое произведение интересно и уникально, но вместе они великолепно дополняют и усиливают друг друга – настоящий эффект синергии. Рами Блект легко и позитивно излагает древнюю мудрость и законы природы, без знания которых невозможно быть гармоничным, здоровым и успешным. Автор помогает отыскать ответ на один из главных человеческих вопросов – как стать счастливым в этом мире. Отличное сочетание теории и практики из реальной жизни. Цель книги – донести уникальные знания до людей, вне зависимости от нации, религии и возраста. Природа так устроена – в ней каждый может быть счастливым и гармоничным.', @P_id_ph, 2020, '9785171175870', '16+');SELECT @P_id_b := id FROM books WHERE name = 'Тройная мудрость';SELECT @P_id_a := id FROM authors WHERE name = 'Блект Рами';INSERT INTO authors_of_the_books (book_id, author_id) VALUES (@P_id_b, @P_id_a);SELECT @P_id_g := id FROM ganres WHERE name = 'Саморазвитие / личностный рост'; INSERT INTO ganres_of_the_books (book_id, ganre_id) VALUES (@P_id_b, @P_id_g);
