SELECT @P_id_ph := id FROM publishing_houses WHERE name = 'Издательство «АСТ»';INSERT INTO books (name, comment, publishing_house_id ,year, ISBN, characteristics) VALUES ('Лабиринт памяти', 'Блуждая по лабиринту памяти, известная актриса, уникальная женщина Алла Демидова вспоминает яркие страницы своей творческой биографии и щедро делится ими с читателем. Вместе с автором мы переносимся в Театр на Таганке и попадаем на репетиции и спектакли Юрия Любимова и Анатолия Эфроса, как живого видим Владимира Высоцкого, затем окунаемся в атмосферу деятельности таких режиссеров, как Роман Виктюк, Лариса Шепитько, Кира Муратова, Андрей Тарковский, Сергей Параджанов, и рядом наблюдаем прекрасных актеров – Иннокентия Смоктуновского, Георгия Жженова… А вот мы уже оказываемся в квартире Лили Брик, овеянной тайной и загадкой. Или следуем за актрисой в ее зарубежных поездках и знакомимся с деятелями западного искусства – Антуаном Витезом, Теодором Терзопулосом, Бобом Уилсоном, Жоржем Сименоном… И конечно, везде мы видим Аллу Демидову, в самых разных театральных и киноролях.', @P_id_ph, 2020, '9785171223397', '12+');SELECT @P_id_b := id FROM books WHERE name = 'Лабиринт памяти';SELECT @P_id_a := id FROM authors WHERE name = 'Демидова А.С.';INSERT INTO authors_of_the_books (book_id, author_id) VALUES (@P_id_b, @P_id_a);SELECT @P_id_g := id FROM ganres WHERE name = 'Биографии и мемуары'; INSERT INTO ganres_of_the_books (book_id, ganre_id) VALUES (@P_id_b, @P_id_g);
