SELECT @P_id_ph := id FROM publishing_houses WHERE name = 'Издательство «АСТ»';INSERT INTO books (name, comment, publishing_house_id ,year, ISBN, characteristics) VALUES ('Аня из Зеленых Мезонинов', 'Один из самых известных романов канадской писательницы Люси Мод Монтгомери (1874 – 1972) – «Аня из Зеленых Мезонинов». Роман вышел в 1908 году и сразу полюбился читателям. Это история про одиннадцатилетнюю сироту Аню Ширли, которая попадает в семью одиноких брата и сестры, Мэтью и Мариллы, на ферму Зеленые Мезонины. Своей добротой, открытостью и мечтательностью девочка может расположить к себе даже самых чёрствых и строгих людей. Вскоре семья фермеров понимает, насколько скучной и однообразной была их жизни без Ани. Еще в начале XX века произведение приобрело большую популярность и по сей продолжает публиковаться во многих странах. По роману сняты фильмы и даже мультфильмы, а дом-музей на острове Принца Эдуарда, посвященный Ане, постоянно посещают толпы туристов-поклонников.
