SELECT @P_id_ph := id FROM publishing_houses WHERE name = 'Издательство «АСТ»';INSERT INTO books (name, comment, publishing_house_id ,year, ISBN, characteristics) VALUES ('Противостояние', 'Стивен Кинг – «Противостояние» Стивен Кинг… «Король ужасов»? Да. Непревзойденный мастер проникновения в самые тайные, самые страшные, самые мрачные уголки человеческой души? Да! Жестокий, яростный и агрессивный эстет тьмы, чьи книги пугают и восхищают, возмущают – и все-таки привлекают? О да!.. Каждый из нас – лишь человек, стоящий над черной, адовой бездной собственных страхов. И только Стивен Кинг – великий Стивен Кинг! – может заставить нас шагнуть в эту бездну…
