SELECT @P_id_ph := id FROM publishing_houses WHERE name = 'Издательство «АСТ»';INSERT INTO books (name, comment, publishing_house_id ,year, ISBN, characteristics) VALUES ('Сплетни нашего городка', 'В маленьком техасском городке Блуме все говорят о Дженни Сью Бейкер. Бывшая школьная королева красоты, богачка, Дженни вышла замуж за респектабельного нью-йоркца. Однако она вернулась обратно в свой маленький городок на старом автобусе, чем вызвала новый взрыв сплетен. Дженни окончательно разругалась с матерью, сняла крохотную комнату и устроилась экономкой к двум невероятно обаятельным старым леди.
