SELECT @P_id_ph := id FROM publishing_houses WHERE name = 'Издательство «Эксмо»';INSERT INTO books (name, comment, publishing_house_id ,year, ISBN, characteristics) VALUES ('Стремление к счастью', 'Людвиг Фейербах – великий и спорный немецкий философ XIX века.
Его концепция, согласно которой христианство – не религия, а форма сознания человека и человечества в целом, поразила современников. Фейербах призывал отказаться от иллюзий, но не впадать в пессимизм, а наоборот, понимать, что сама природа устроена так, чтобы дать каждому из нас счастье.
