SELECT @P_id_ph := id FROM publishing_houses WHERE name = 'Издательство «АСТ»';INSERT INTO books (name, comment, publishing_house_id ,year, ISBN, characteristics) VALUES ('Герой нашего времени', '«Герой нашего времени» – программное произведение классика русской и мировой литературы М.Ю. Лермонтова (1814 – 1841). «Герой нашего времени» – первое в русской литературе произведение в жанре реалистического социально-психологического романа. Впервые роман был опубликован в 1840 г. и стал ярким явлением, одной из вершин в отечественной литературе ХIХ века. Роман «Герой нашего времени» входит в школьную программу по литературе для старшеклассников.
