SELECT @P_id_ph := id FROM publishing_houses WHERE name = 'Издательство «АСТ»';INSERT INTO books (name, comment, publishing_house_id ,year, ISBN, characteristics) VALUES ('Нас всех тошнит. Как театр стал современным, а мы этого не заметили', 'Как так вышло, что приглашение пойти на спектакль теперь может привести вас на железнодорожный вокзал, заброшенный завод или автозаправку? Откуда на сцене появились роботы и куда исчезает сама сцена? Спектакли идут по несколько дней, а зритель часто вынужден сам в нём играть, и у него выходит лучше, чем у профессиональных актёров; вас водят по улицам и особнякам, предлагают потанцевать в метро, заполнить анкету и самим решить, что вы только что посмотрели. В танце могут стоять на месте, опера может быть современной, что происходит? Театр – это же когда на сцене играют на разрыв аорты классические тексты?
