INSERT INTO books_on_hands (book_id, user_id, loan_date)
SELECT book_id, user_id, '2020-01-01' + INTERVAL RAND()*242 DAY AS loan_date
FROM(
	SELECT DISTINCT
		ROUND(1 + RAND() * 42) AS user_id,
		ROUND(1 + RAND() * 331) AS book_id
	FROM (SELECT '0' AS Number 
			UNION ALL SELECT '1'
			UNION ALL SELECT '2'
			UNION ALL SELECT '3'
			UNION ALL SELECT '4'
			UNION ALL SELECT '5'
			UNION ALL SELECT '6'
			UNION ALL SELECT '7'
			UNION ALL SELECT '8'
			UNION ALL SELECT '9') AS Numbers1
			CROSS JOIN
			(SELECT '0' AS Number 
			UNION ALL SELECT '1'
			UNION ALL SELECT '2'
			UNION ALL SELECT '3'
			UNION ALL SELECT '4'
			UNION ALL SELECT '5'
			UNION ALL SELECT '6'
			UNION ALL SELECT '7'
			UNION ALL SELECT '8'
			UNION ALL SELECT '9')  AS Numbers2 
			CROSS JOIN
			(SELECT '0' AS Number 
			UNION ALL SELECT '1'
			UNION ALL SELECT '2'
			UNION ALL SELECT '3'
			UNION ALL SELECT '4'
			UNION ALL SELECT '5'
			UNION ALL SELECT '6'
			UNION ALL SELECT '7'
			UNION ALL SELECT '8'
			UNION ALL SELECT '9')  AS Numbers3 ) AS tmp_table
JOIN users AS u ON u.id = tmp_table.user_id
JOIN books AS b ON b.id = tmp_table.book_id;

-- Дату возврата книг нужно установить на 2 недели позже, чем дата выдачи.
UPDATE books_on_hands SET borrow_date = loan_date + INTERVAL 14 DAY;

